import React from 'react';
import { useParams } from 'react-router-dom';
import {
  Form,
} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Play () {
  const params = useParams();
  const token = localStorage.getItem('token');
  const sessionid = params.sessionid
  const playerid = localStorage.getItem('playerid')
  var theanswers = React.useState([1, 1]);
  const [show, setShow] = React.useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const [answer, setAnswer] = React.useState([]);
  const getsession = async (id) => {
    const response = await fetch('http://localhost:5005/admin/session/' + id + '/status', {
      method: 'GET',
      headers: {
        'Content-type': 'application/json',
        Authorization: 'Bearer ' + token,
      }
    })
    const data = await response.json();
    localStorage.setItem('session', JSON.stringify(data))
  }
  const uploadanswer = async () => {
    var answerIds = []
    for (var i = 0; i < question.answer.length; i++) {
      for (var j = 0; j < answer.length; j++) {
        if (answer[j] === question.answer[i]) {
          answerIds.push(i)
        }
      }
    }
    const response = await fetch('http://localhost:5005/play/' + playerid + '/answer', {
      method: 'PUT',
      headers: {
        'Content-type': 'application/json',
      },
      body: JSON.stringify({
        answerIds,
      }),
    })
    const data = await response.json();
    localStorage.setItem('session', JSON.stringify(data))
  }
  getsession(sessionid)
  const session = JSON.parse(localStorage.getItem('session'))
  const question = session.questions[session.position]
  theanswers = question.answer
  const time = 10
  const [timing, setTiming] = React.useState(false);
  const [second, setSecond] = React.useState(time);
  React.useEffect(() => {
    let timer;
    function countdown () {
      setSecond((preSecond) => {
        if (preSecond <= 1) {
          setTiming(false);
          return time;
        } else {
          timer = setTimeout(countdown, 1000);
          return preSecond - 1;
        }
      });
    }
    if (timing) {
      timer = setTimeout(countdown, 1000);
    }
    return () => clearTimeout(timer);
  }, [timing]);
  if (question.type === 'multiple choice') {
    handleClose()
  } else {
    handleShow()
  }
  return <>
    <div className='Title'><h1>Playing</h1></div>
    <button disabled={timing} onClick={() => setTiming(true)}>
      {timing ? 'Timing' + second : 'Go'}
    </button>
    <div className='Container'>
      <Form.Group className="mb-3">
        <Form.Label>Question: </Form.Label>
        <Form.Text>{question.text}</Form.Text>
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Image: </Form.Label>
        <img src={question.image} />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Choose your answer</Form.Label>
        <Form.Text>{question.type}</Form.Text>
        <Form.Select show={show} onChange={e => { setAnswer(e.target.value); uploadanswer(e) }}>
          {theanswers.map((item, index) => (
            <option key={index}>{item}</option>
          ))}
        </Form.Select>
        <Form.Select show={!show} multiple='multiple' onChange={e => { setAnswer(e.target.value); uploadanswer(e) }}>
          {theanswers.map((item, index) => (
            <option key={index}>{item}</option>
          ))}
        </Form.Select>
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Answer: </Form.Label>
        <Form.Text></Form.Text>
      </Form.Group>
    </div>
  </>
}
export default Play;
