import React from 'react';
import { useParams } from 'react-router-dom';
import {
  Form,
} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Play () {
  const params = useParams();
  const token = localStorage.getItem('token');
  const sessionid = params.sessionid
  const playerid = localStorage.getItem('playerid')
  var theanswers = React.useState([]);
  const [answer, setAnswer] = React.useState([]);
  const getsession = async (id) => {
    const response = await fetch('http://localhost:5005/admin/session/' + id + '/status', {
      method: 'GET',
      headers: {
        'Content-type': 'application/json',
        Authorization: 'Bearer ' + token,
      }
    })
    const data = await response.json();
    localStorage.setItem('session', JSON.stringify(data))
  }
  const uploadanswer = async () => {
    var answerIds = []
    for (var i = 0; i < question.answer.length; i++) {
      for (var j = 0; j < answer.length; j++) {
        if (answer[j] === question.answer[i]) {
          answerIds.push(i)
        }
      }
    }
    await fetch('http://localhost:5005/play/' + playerid + '/answer', {
      method: 'PUT',
      headers: {
        'Content-type': 'application/json',
      },
      body: JSON.stringify({
        answerIds,
      }),
    })
  }
  getsession(sessionid)
  const session = JSON.parse(localStorage.getItem('session'))
  const position = session.results.position
  const question = session.results.questions[position]
  var show = React.useState('none');
  var show1 = React.useState('none');
  if (question.type === 'single choice') {
    show = 'block'
    show1 = 'none'
  } else {
    show1 = 'block'
    show = 'none'
  }
  const ifshow = {
    display: show
  }
  const ifshow1 = {
    display: show1
  }
  theanswers = question.answer
  const time = 5
  const [timerID, setTimerID] = React.useState(null);
  const [counter, setCounter] = React.useState(time);
  useEffect(() => {
    if (counter > 0) {
      let timer = setTimeout(() => {
        setCounter(counter - 1)
      }, 1000);
      setTimerID(timer)
    } else {
      props.history.push('/order')
    }
    return () => {
      setTimerID(null)
    }
  }, [counter]);
  console.log(timerID)
  return <>
    <div className='Title'><h1>Playing</h1></div>
    <button>
      your time:{counter}
    </button>
    <div className='Container'>
      <Form.Group className="mb-3">
        <Form.Label>Question: </Form.Label>
        <Form.Text>{question.text}</Form.Text>
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Image: </Form.Label>
        <img src={question.image} />
      </Form.Group>
      <Form.Group style={ifshow}>
        <Form.Label>Choose your answer</Form.Label>
        <Form.Text>{question.type}</Form.Text>
        <Form.Select onChange={e => { setAnswer(e.target.value); uploadanswer() }}>
          {theanswers.map((item, index) => (
            <option key={index}>{item}</option>
          ))}
        </Form.Select>
      </Form.Group>
      <Form.Group style={ifshow1}>
        <Form.Label>Choose your answer</Form.Label>
        <Form.Text>{question.type}</Form.Text>
        {theanswers.map((item, index) => (
          <Form.Check
          label={item}
          type='checkbox'
          key={index + 10}
        />
        ))}
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Answer: </Form.Label>
        <Form.Text></Form.Text>
      </Form.Group>
    </div>
  </>
}
export default Play;
