import React from 'react';
import { useNavigate } from 'react-router-dom';

function LoginForm () {
  const [email, setEmail] = React.useState('');
  const [password, setPassword] = React.useState('');
  const navigate = useNavigate();

  const login = async () => {
    try {
      const response = await fetch('http://localhost:5005/admin/auth/login', {
        method: 'POST',
        headers: {
          'Content-type': 'application/json',
        },
        body: JSON.stringify({
          email,
          password
        })
      });
      const data = await response.json();
      localStorage.setItem('token', data.token)
      navigate('/dashboard');
    } catch (err) {
      alert(err)
    }
  }
  return (
    <>
      Email: <input type='text' onChange={e => setEmail(e.target.value)}/><br />
      Password: <input type='text' onChange={e => setPassword(e.target.value)}/><br />
      <button onClick={login}>Login</button>
      <div className='Container'>
      <Form.Group className="mb-3">
        <Form.Label>Email: </Form.Label>
        <Form.Control placeholder="email" type='text' onChange={e => setEmail(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Password: </Form.Label>
        <Form.Control placeholder="password" type='text' onChange={e => setPassword(e.target.value)} />
      </Form.Group>
      <Button variant="success" type="submit" onClick={login}>Login</Button>
    </div>
    </>);
}

export default LoginForm;
