import React from 'react';
import { useParams, Link } from 'react-router-dom';
import {
  Card,
  Row,
  Col,
  Button
} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Editquiz () {
  const params = useParams();
  const id = params.quizid
  console.log(id)
  var questions = React.useState('');
  const token = localStorage.getItem('token');
  const getquiz = async (id) => {
    const response = await fetch('http://localhost:5005/admin/quiz/' + id, {
      method: 'GET',
      headers: {
        'Content-type': 'application/json',
        Authorization: 'Bearer ' + token,
      }
    })
    const data = await response.json();
    const questions = data.questions
    localStorage.setItem('questions', JSON.stringify(questions))
  }
  getquiz(id)
  questions = JSON.parse(localStorage.getItem('questions'))
  const fresh = async (id) => {
    await getquiz(id)
    location.reload()
  }
  const deleteQuestion = async (id) => {
    const num = questions.length
    for (var i = 0; i < num; i++) {
      if (questions[i].id === id) {
        questions.splice(i, 1)
      }
    }
    await fetch('http://localhost:5005/admin/quiz/' + id, {
      method: 'PUT',
      headers: {
        'Content-type': 'application/json',
        Authorization: 'Bearer ' + token,
      },
      body: JSON.stringify({ questions })
    })
    fresh(id)
  }
  return <>
    <h1>Edit quiz</h1>
    <Row xs={1} md={4} className="g-4">
        {questions.map(question => (
          <Col key={question.id}>
            <Card>
              <Card.Img variant="top" src={question.image} />
              <Card.Body>
                <Card.Title>{question.type}</Card.Title>
                <Card.Text>
                  question: {question.text}
                </Card.Text>
                <Card.Text>
                  time limit: {question.time}
                </Card.Text>
                <Card.Text>
                  value {question.value}
                </Card.Text>
                <Card.Text>
                  answer: {question.answer}
                </Card.Text>
                <Button variant="outline-primary" size="sm" href={`/editquestion:${question.id}`}>Edit</Button>
                <br />
                <Button variant="outline-secondary" size="sm" onClick={deleteQuestion(question.id)}>Delete</Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      <br />
      <h1>hi</h1>
      <Link to='question/new'>Create new question</Link>
  </>
}
export default Editquiz;
