import React from 'react';
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import {
  Form,
} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Play () {
  const params = useParams();
  const token = localStorage.getItem('token');
  const sessionid = params.sessionid
  const playerid = localStorage.getItem('playerid')
  const navigate = useNavigate();
  const [show, setShow] = React.useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const [answer, setAnswer] = React.useState([]);
  const getsession = async (id) => {
    const response = await fetch('http://localhost:5005/admin/session/' + id + '/status', {
      method: 'GET',
      headers: {
        'Content-type': 'application/json',
        Authorization: 'Bearer ' + token,
      }
    })
    const data = await response.json();
    localStorage.setItem('session', JSON.stringify(data))
  }
  getsession(sessionid)
  const session = JSON.parse(localStorage.getItem('session'))
  const question = session.questions[session.position]
  if (question.type === 'multiple choice') {
    handleClose()
  } else {
    handleShow()
  }
  return <>
    <div className='Title'><h1>Playing</h1></div>
    <div className='Container'>
      <Form.Group className="mb-3">
        <Form.Label>Question: </Form.Label>
        <Form.Text>{question.text}</Form.Text>
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Image: </Form.Label>
        <img src={question.image} />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Choose your answer</Form.Label>
        <Form.Text>{question.type}</Form.Text>
        <Form.Select show={show} multiple='multiple' onChange={e => setAnswer(e.target.value)}>
          {question.answer.map((item, index) => {
            <option key={index}>{item}</option>
          })}
        </Form.Select>
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Answer: </Form.Label>
        <Form.Control placeholder="url" type='text' onChange={e => setVideo(e.target.value)} />
      </Form.Group>
    </div>
  </>
}
export default Play;
