import React from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Card,
  Row,
  Col,
  Button,
} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Dashboard () {
  const navigate = useNavigate();
  const [name, setName] = React.useState('');
  const token = localStorage.getItem('token');
  React.useEffect(() => {
    if (!token) {
      navigate('/login')
    }
  });
  const createQuiz = async () => {
    try {
      await fetch('http://localhost:5005/admin/quiz/new', {
        method: 'POST',
        headers: {
          'Content-type': 'application/json',
          Authorization: 'Bearer ' + token,
        },
        body: JSON.stringify({ name }),
      });
    } catch (err) {
      alert(err)
    }
  }
  const getquizzes = async () => {
    const response = await fetch('http://localhost:5005/admin/quiz', {
      method: 'GET',
      headers: {
        'Content-type': 'application/json',
        Authorization: 'Bearer ' + token,
      }
    })
    const data = await response.json();
    const body = data.quizzes
    const num = body.length;
    const quizzes = [];
    for (var i = 0; i < num; i++) {
      const getquiz = async (id) => {
        const response = await fetch('http://localhost:5005/admin/quiz/' + id, {
          method: 'GET',
          headers: {
            'Content-type': 'application/json',
            Authorization: 'Bearer ' + token,
          }
        })
        const data = await response.json();
        localStorage.setItem('quiz', JSON.stringify(data))
      }
      await getquiz(body[i].id);
      const quiz = localStorage.getItem('quiz')
      quizzes.push(JSON.parse(quiz));
    }
    localStorage.setItem('quizzes', JSON.stringify(quizzes))
  }
  getquizzes()
  const quizzes = JSON.parse(localStorage.getItem('quizzes'))

  return (
    <>
      <h1>Dashboard</h1>
      <Row xs={1} md={2} className="g-4">
        {quizzes.map(quiz => (
          <Col key={quiz.name}>
            <Card>
              <Card.Img variant="top" src={quiz.thumbnail} />
              <Card.Body>
                <Card.Title>{quiz.name}</Card.Title>
                <Card.Text>
                  questions: {quiz.questions.length}
                </Card.Text>
                <Button variant="primary">Edit</Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      <br />
      Quiz name: <input type="text" onChange={e => setName(e.target.value)}></input>
      <button onClick={createQuiz}>Create</button>
    </>);
}

export default Dashboard;
