import React from 'react';
import {
  Form,
  Button,
} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useParams } from 'react-router-dom';

function Joinplay () {
  const [name, setName] = React.useState('');
  const [id, setId] = React.useState('');
  const params = useParams();
  if (params) {
    const id1 = params.sessionid
    const [id, setId] = React.useState(id1);
  } else {
    const [id, setId] = React.useState('');
  }
  const join = async () => {
    try {
      const response = await fetch('http://localhost:5005/play/join/' + id, {
        method: 'POST',
        headers: {
          'Content-type': 'application/json'
        },
        body: JSON.stringify({
          name,
        })
      });
      alert('joined!')
      const data = await response.json()
      console.log(data)
      localStorage.setItem('playerid', data.playerId)
    } catch (err) {
      console.log(err)
    }
  }
  return <>
    <div className='Title'><h1>Join play</h1></div>
    <div className='Container'>
      <Form.Group className="mb-3">
        <Form.Label>Session id: </Form.Label>
        <Form.Control placeholder="id" type='text' value={id} onChange={e => setId(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Your name: </Form.Label>
        <Form.Control placeholder="name" type='text' onChange={e => setName(e.target.value)} />
      </Form.Group>
        <br/>
      <Button variant="success" type="submit" onClick={join}>Join</Button>
    </div>
  </>
}
export default Joinplay;
