import React from 'react';
import { useParams } from 'react-router-dom';
import {
  Table,
} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Getresult () {
  const params = useParams();
  const id = params.quizid;
  const token = localStorage.getItem('token');
  var sessions = React.useState();
  const getquiz = async (id) => {
    const response = await fetch('http://localhost:5005/admin/quiz/' + id, {
      method: 'GET',
      headers: {
        'Content-type': 'application/json',
        Authorization: 'Bearer ' + token,
      }
    })
    const data = await response.json();
    const session = data.oldSessions
    localStorage.setItem('sessions', JSON.stringify(session))
  }
  getquiz(id)
  sessions = JSON.parse(localStorage.getItem('sessions'))
  const getsession = async (id) => {
    const response = await fetch('http://localhost:5005/admin/session/' + id + '/results', {
      method: 'GET',
      headers: {
        'Content-type': 'application/json',
        Authorization: 'Bearer ' + token,
      }
    })
    const data = await response.json();
    localStorage.setItem('session', JSON.stringify(data.results))
  }
  var mysessions = []
  const getsessions = async () => {
    for (var i = 0; i < sessions.length; i++) {
      await getsession(sessions[i])
      const session = JSON.parse(localStorage.getItem('session'))
      mysessions.push(session)
    }
  }
  getsessions()
  console.log(mysessions)
  function sum (arr) {
    var s = 0
    for (var i = 0; i < arr.length; i++) {
      if (arr[i].correct) {
        s += arr[i].correct 
      }
    }
    return s
  }
  return <>
    <div className='Title'><h1>Result</h1></div>
    <Table striped bordered hover>
      <thead>
        <tr>
          <th>#</th>
          <th>First Name</th>
          <th>Score</th>
        </tr>
      </thead>
      <tbody>
        {mysessions.map((item, index) => (
          <tr key={index}>
            <td>{index}</td>
            <td>{item.name}</td>
            <td>{sum(item.answers)}</td>
          </tr>
        ))}
      </tbody>
    </Table>
  </>
}
export default Getresult;
