import React from 'react';
import { useParams } from 'react-router-dom';
import {
  Form,
  Button,
  Row,
  Col
} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Editquestion () {
  const params = useParams();
  const id = params.quizid
  const questionid = params.questionid
  const token = localStorage.getItem('token');
  var questions = React.useState('');
  var question = {};
  const getquiz = async (id) => {
    const response = await fetch('http://localhost:5005/admin/quiz/' + id, {
      method: 'GET',
      headers: {
        'Content-type': 'application/json',
        Authorization: 'Bearer ' + token,
      }
    })
    const data = await response.json();
    const questions = data.questions
    localStorage.setItem('questions', JSON.stringify(questions))
  }
  getquiz(id)
  questions = JSON.parse(localStorage.getItem('questions'))
  const thequestion = questions[questionid]
  const [type, setType] = React.useState(thequestion.type);
  const [text, setText] = React.useState(thequestion.text);
  const [time, setTime] = React.useState(thequestion.time);
  const [value, setValue] = React.useState(thequestion.value);
  const [image, setImage] = React.useState(thequestion.image);
  const [answer, setAnswer] = React.useState(thequestion.answer);
  const [video, setVideo] = React.useState(thequestion.video);
  const updata = async () => {
    const questionid = questions.length
    question.id = questionid
    question.type = type
    question.text = text
    question.time = time
    question.value = value
    question.image = image
    question.video = video
    question.answer = answer
    questions.push(question)
    try {
      await fetch('http://localhost:5005/admin/quiz/' + id, {
        method: 'PUT',
        headers: {
          'Content-type': 'application/json',
          Authorization: 'Bearer ' + token,
        },
        body: JSON.stringify({ questions })
      });
      alert('updated!')
    } catch (err) {
      alert(err)
    }
  }
  var list1 = []
  for (var i = 0; i < answer.length; i++) {
    list1.push(1)
  }
  var [list, setList] = React.useState(list1);
  const add = () => {
    if (list.length === 6) {
      alert('no more')
    } else {
      setList([...list, 1])
    }
  }
  return <>
    <div className='Title'><h1>Edit question</h1></div>
    <div className='Container'>
      <Form.Group className="mb-3">
        <Form.Label>Question Type:</Form.Label>
        <Form.Select value={type} onChange={e => setType(e.target.value)}>
          <option>please choose question type</option>
          <option>multiple choice</option>
          <option>single choice</option>
        </Form.Select>
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Question: </Form.Label>
        <Form.Control placeholder="text" type='text' value={text} onChange={e => setText(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Time Limit: </Form.Label>
        <Form.Control placeholder="number of minutes" type='text' value={time} onChange={e => setTime(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Question Value: </Form.Label>
        <Form.Control placeholder="number of question value" type='text' value={value} onChange={e => setValue(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Image: </Form.Label>
        <Form.Control type="file" value={image} accept="image/png, image/jpeg, image/jpg" placeholder="question" onChange={e => setImage(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Video: </Form.Label>
        <Form.Control placeholder="url" type='text' value={video} onChange={e => setVideo(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Answer: </Form.Label>
        <Row xs={1} md={2} className="g-4">
          {list.map((item, index) => (
            <Col key={index}>
              <Form.Control placeholder={`input your answer${index + 1}`} value={answer[index]} type='text' id={item} onChange={e => setAnswer([...answer, e.target.value])} />
            </Col>
          ))}
        </Row>
        <br/>
        <Button variant="outline-secondary" size='sm' type="submit" onClick={add}>Add more</Button>
      </Form.Group>
      <Button variant="success" type="submit" onClick={updata}>Update</Button>
    </div>
  </>
}
export default Editquestion;
