import React from 'react';
import { useParams } from 'react-router-dom';
import {
  Form,
} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Newquestion () {
  const params = useParams();
  const id = params.quizid;
  console.log(id)
  const token = localStorage.getItem('token');
  var questions = React.useState('');
  var question = {};
  const [type, setType] = React.useState('');
  const [text, setText] = React.useState('');
  const [time, setTime] = React.useState('');
  const [value, setValue] = React.useState('');
  const [image, setImage] = React.useState('');
  const [answer, setAnswer] = React.useState('');
  const getquiz = async (id) => {
    const response = await fetch('http://localhost:5005/admin/quiz/' + id, {
      method: 'GET',
      headers: {
        'Content-type': 'application/json',
        Authorization: 'Bearer ' + token,
      }
    })
    const data = await response.json();
    const questions = data.questions
    localStorage.setItem('questions', JSON.stringify(questions))
  }
  getquiz(id)
  questions = JSON.parse(localStorage.getItem('questions'))
  const create = async () => {
    question.type = type
    question.text = text
    question.time = time
    question.value = value
    question.image = image
    question.answer = answer
    questions.push(question)
    await fetch('http://localhost:5005/admin/quiz' + id, {
      method: 'PUT',
      headers: {
        'Content-type': 'application/json',
        Authorization: 'Bearer ' + token,
      },
      body: JSON.stringify(questions)
    });
  }

  return <>
    <div className='Title'><h1>Create new question</h1></div>
    Question Type: <input type='text' onChange={e => setType(e.target.value)} /><br />
    Question: <input type='text' onChange={e => setText(e.target.value)} /><br />
    Time Limit: <input type='text' onChange={e => setTime(e.target.value)} /><br />
    Question Value: <input type='text' onChange={e => setValue(e.target.value)} /><br />
    Image or Video: <input type='text' onChange={e => setImage(e.target.value)} /><br />
    Answer: <input type='text' onChange={e => setAnswer(e.target.value)} /><br />
    <button onClick={create}>Create</button>
    <div className='Container'>
      <Form.Group className="mb-3">
        <Form.Label>Question Type:</Form.Label>
        <Form.Select>
          <option>m</option>
          <option>s</option>
        </Form.Select>
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Disabled input</Form.Label>
        <Form.Control placeholder="Disabled input" disabled />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Check type="checkbox" label="Can't check this" disabled />
      </Form.Group>
    </div>
  </>
}
export default Newquestion;
