import React from 'react';
import { useParams } from 'react-router-dom';
import {
  Form,
  Button,
} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Newquestion () {
  const params = useParams();
  const id = params.quizid;
  const token = localStorage.getItem('token');
  var questions = React.useState('');
  var question = {};
  const [type, setType] = React.useState('');
  const [text, setText] = React.useState('');
  const [time, setTime] = React.useState('');
  const [value, setValue] = React.useState('');
  const [image, setImage] = React.useState('');
  const [answer, setAnswer] = React.useState('');
  const [video, setVideo] = React.useState('');
  const getquiz = async (id) => {
    const response = await fetch('http://localhost:5005/admin/quiz/' + id, {
      method: 'GET',
      headers: {
        'Content-type': 'application/json',
        Authorization: 'Bearer ' + token,
      }
    })
    const data = await response.json();
    const questions = data.questions
    localStorage.setItem('questions', JSON.stringify(questions))
  }
  getquiz(id)
  questions = JSON.parse(localStorage.getItem('questions'))
  const create = async () => {
    question.type = type
    question.text = text
    question.time = time
    question.value = value
    question.image = image
    question.video = video
    question.answer = answer
    questions.push(question)
    await fetch('http://localhost:5005/admin/quiz' + id, {
      method: 'PUT',
      headers: {
        'Content-type': 'application/json',
        Authorization: 'Bearer ' + token,
      },
      body: JSON.stringify(questions)
    });
  }
  var list = [1, 1]
  const add = () => {
    list.append(1)
  }

  return <>
    <div className='Title'><h1>Create new question</h1></div>
    <div className='Container'>
      <Form.Group className="mb-3">
        <Form.Label>Question Type:</Form.Label>
        <Form.Select onChange={e => setType(e.target.value)}>
          <option>please choose question type</option>
          <option>multiple choice</option>
          <option>single choice</option>
        </Form.Select>
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Question: </Form.Label>
        <Form.Control placeholder="text" type='text' onChange={e => setText(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Time Limit: </Form.Label>
        <Form.Control placeholder="number of minutes" type='text' onChange={e => setTime(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Question Value: </Form.Label>
        <Form.Control placeholder="number of question value" type='text' onChange={e => setValue(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Image: </Form.Label>
        <Form.Control type="file" accept="image/png, image/jpeg, image/jpg" placeholder="question" onChange={e => setImage(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Video: </Form.Label>
        <Form.Control placeholder="url" type='text' onChange={e => setVideo(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Answer: </Form.Label>
        <Row xs={1} md={2} className="g-4">
          {list.map(val, ind => (
            <Col key={ind}>
              <Form.Control placeholder="input your answers" type='text' onChange={e => setAnswer(e.target.value)} />
            </Col>
          ))}
        <Button variant="outline-secondary" size='sm' type="submit" onClick={add}>Add more</Button>
        </Row>
      </Form.Group>
      <Button variant="success" type="submit" onClick={create}>Create</Button>
    </div>
  </>
}
export default Newquestion;
