import React from 'react';
import { useNavigate, Link } from 'react-router-dom';
import {
  Card,
  Row,
  Col,
  Button
} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Dashboard () {
  const navigate = useNavigate();
  const [name, setName] = React.useState('');
  const [id, setId] = React.useState('');
  var quizzes = React.useState('');
  const token = localStorage.getItem('token');
  React.useEffect(() => {
    if (!token) {
      navigate('/login')
    }
  });
  const createQuiz = async () => {
    try {
      await fetch('http://localhost:5005/admin/quiz/new', {
        method: 'POST',
        headers: {
          'Content-type': 'application/json',
          Authorization: 'Bearer ' + token,
        },
        body: JSON.stringify({ name }),
      });
      fresh()
    } catch (err) {
      alert(err)
    }
  }
  const deleteQuiz = async () => {
    try {
      await fetch('http://localhost:5005/admin/quiz/' + id, {
        method: 'DELETE',
        headers: {
          'Content-type': 'application/json',
          Authorization: 'Bearer ' + token,
        }
      });
      fresh()
    } catch (err) {
      alert(err)
    }
  }
  const getquizzes = async () => {
    const response = await fetch('http://localhost:5005/admin/quiz', {
      method: 'GET',
      headers: {
        'Content-type': 'application/json',
        Authorization: 'Bearer ' + token,
      }
    })
    const data = await response.json();
    const body = data.quizzes
    const num = body.length
    const quizzes1 = []
    for (var i = 0; i < num; i++) {
      const getquiz = async (id) => {
        const response = await fetch('http://localhost:5005/admin/quiz/' + id, {
          method: 'GET',
          headers: {
            'Content-type': 'application/json',
            Authorization: 'Bearer ' + token,
          }
        })
        const data = await response.json();
        localStorage.setItem('quiz', JSON.stringify(data))
        localStorage.setItem('id', JSON.stringify(id))
      }
      await getquiz(body[i].id);
      var quiz = localStorage.getItem('quiz')
      quiz = JSON.parse(quiz)
      quiz.id = localStorage.getItem('id')
      quizzes1.push(quiz);
    }
    localStorage.setItem('quizzes', JSON.stringify(quizzes1))
  }
  function renderdash () {
    getquizzes()
    quizzes = JSON.parse(localStorage.getItem('quizzes'))
  }
  const fresh = async () => {
    await getquizzes()
    quizzes = JSON.parse(localStorage.getItem('quizzes'))
    location.reload()
  }
  renderdash()

  return (
    <>
      <h1>Dashboard</h1>
      <Row xs={1} md={4} className="g-4">
        {quizzes.map(quiz => (
          <Col key={quiz.id}>
            <Card>
              <Card.Img variant="top" src={quiz.thumbnail} />
              <Card.Body>
                <Card.Title>{quiz.name}</Card.Title>
                <Card.Text>
                  questions: {quiz.questions.length}
                </Card.Text>
                <Link to={`/editquiz:${quiz.id}`}>Edit</Link>
                <Button variant="link" onClick={deleteQuiz}>Delete</Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      <br />
      Quiz name: <input type="text" onChange={e => setName(e.target.value)}></input>
      <button onClick={createQuiz}>Create</button>
      <br />
      <br />
      Quiz id: <input type="text" onChange={e => setId(e.target.value)}></input>
      <button onClick={deleteQuiz}>Delete</button>
    </>);
}

export default Dashboard;
