import React from 'react';
import {
  Form,
  Button,
} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useParams, useNavigate } from 'react-router-dom';

function Joinplay () {
  const [name, setName] = React.useState('');
  const [id, setId] = React.useState('');
  const navigate = useNavigate();
  const params = useParams();
  var id1 = ''
  if (params) {
    id1 = params.sessionid
  }
  const join = async () => {
    var theid = id1
    if (id) {
      theid = id
    }
    try {
      const response = await fetch('http://localhost:5005/play/join/' + theid, {
        method: 'POST',
        headers: {
          'Content-type': 'application/json'
        },
        body: JSON.stringify({
          name,
        })
      });
      alert('joined!')
      const data = await response.json()
      localStorage.setItem('playerid', data.playerId)
      navigate('/play' + theid)
    } catch (err) {
      console.log(err)
    }
  }
  return <>
    <div className='Title'><h1>Join play</h1></div>
    <div className='Container'>
      <Form.Group className="mb-3">
        <Form.Label>Session id: </Form.Label>
        <Form.Control placeholder="id" type='text' value={id1} onChange={e => setId(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Your name: </Form.Label>
        <Form.Control placeholder="name" type='text' onChange={e => setName(e.target.value)} />
      </Form.Group>
        <br/>
      <Button variant="success" type="submit" onClick={join}>Join</Button>
    </div>
  </>
}
export default Joinplay;
