import React from 'react';
// import { useNavigate } from 'react-router-dom';
import {
//   Card,
  Row,
  Col,
  Button,
  Form,
//   Modal
} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import FloatingLabel from 'react-bootstrap/FloatingLabel';
// import copy from 'copy-to-clipboard';

function CreateRecipe () {
  // const navigate = useNavigate();
  // const [curr, setCurr] = React.useState(0);
  // const go = (val) => {
  //   setCurr(Number(curr) + val);
  // };
  const [show1, setS1] = React.useState('block');
  const [show2, setS2] = React.useState('none');
  const [show3, setS3] = React.useState('none');
  const [show4, setS4] = React.useState('none');
  function click1 () {
    setS1('block')
    setS2('none')
    setS3('none')
    setS4('none')
  }
  function click2 () {
    setS1('none')
    setS2('block')
    setS3('none')
    setS4('none')
  }
  function click3 () {
    setS1('none')
    setS2('none')
    setS3('block')
    setS4('none')
  }
  function click4 () {
    setS1('none')
    setS2('none')
    setS3('none')
    setS4('block')
  }
  const ifshow1 = {
    display: show1
  }
  const ifshow2 = {
    display: show2
  }
  const ifshow3 = {
    display: show3
  }
  const ifshow4 = {
    display: show4
  }
  const list1 = [[1]]
  // for (let i = 0; i < answer.length; i++) {
  //   list1.push(1)
  // }
  const [list, setList] = React.useState(list1);
  // const addingre = (i) => {
  //   setList([...list[i], 1])
  // }
  const addgroup = (i) => {
    setList([...list, [1]])
  }
  return (
    <>
      <div className='personinfo'>personal information</div>
      <br />
      <div className='createinfo'>
      <Button variant="outline-info" onClick={click1}>Recipe Details</Button>
      <Button variant="outline-info" onClick={click2}>Ingredients</Button>
      <Button variant="outline-info" onClick={click3}>Cooking Steps</Button>
      <Button variant="outline-info" onClick={click4}>Complete Creation</Button>
      </div>
      <div style={ifshow1}>
        <div className='createstep'>
          <div className='columnleft'>
            <div className='Container1'>
              <Form.Group className="mb-3">
                <Form.Label>Recipe Name: </Form.Label>
                <Form.Control placeholder="recipe name" type='text'/>
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Category: </Form.Label>
                <Form.Select aria-label="Default select example">
                  <option>Open to select</option>
                  <option value="1">One</option>
                  <option value="2">Two</option>
                  <option value="3">Three</option>
                </Form.Select>
              </Form.Group>
            </div>
          </div>
      <div className='columnright'>
        <Form.Group className="mb-3">
        <Form.Label>Description: </Form.Label>
        <FloatingLabel controlId="floatingTextarea2">
        <Form.Control
          as="textarea"
          placeholder="description"
          style={{ height: '150px' }}
        />
        </FloatingLabel>
        </Form.Group>
      </div>
      <div className='createfoot'>
        <Button onClick={click2}>next</Button>
      </div>
      </div>
      </div>
      <div style={ifshow2}>
        <div className='createstep'>
          <div className='Container2'>
            <h3>Indredient Details</h3>
            <Form.Group className="mb-3">
              {list.map((item, index) => (
                <div key={index} id={item}>
                  <Row>
                    <Form.Label>Group Title {index + 1}: </Form.Label>
                    <Form.Control placeholder="group name" type='text'/>
                  </Row>
                  <Row className="align-items-center">
                    {list[index].map((item1, index1) => (
                      <Col key={index1} sm={6} id={item1}>
                      <Form.Control type="email" placeholder={`ingredient${index1}`} />
                      </Col>
                    ))}
                    <Col sm={2}>
                      <Button type='submit' onClick={addingre(index)}>add</Button>
                    </Col>
                  </Row>
                </div>
              ))}
            </Form.Group>
            <Form.Group className="mb-3" controlId="formHorizontalEmail">
            </Form.Group>
            <Form.Group className="mb-3">
              <Button type='submit' onClick={addgroup}>Add New Group</Button>
            </Form.Group>
          </div>
        </div>
      </div>
      <div style={ifshow3}>123</div>
      <div style={ifshow4}>123</div>
    </>);
}
export default CreateRecipe;
