import React from 'react';

function Joinplay () {
  const [id, setId] = React.useState('');
  const [name, setName] = React.useState('');
  const join = async (id) => {
    try {
      await fetch('http://localhost:5005/play/join/' + id, {
        method: 'PUT',
        headers: {
          'Content-type': 'application/json',
          Authorization: 'Bearer ' + token,
        },
        body: JSON.stringify({
          name,
        })
      });
      alert('joined!')
    } catch (err) {
      alert(err)
    }
  }
  return <>
    <div className='Title'><h1>Join play</h1></div>
    <div className='Container'>
      <Form.Group className="mb-3">
        <Form.Label>Session id: </Form.Label>
        <Form.Control placeholder="text" type='text' onChange={e => setId(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Your name: </Form.Label>
        <Form.Control placeholder="number of minutes" type='text' onChange={e => setName(e.target.value)} />
      </Form.Group>
        <br/>
      <Button variant="success" type="submit" onClick={join(id)}>Join</Button>
    </div>
  </>
}
export default Joinplay;
