import React from 'react';
import { useParams } from 'react-router-dom';
import {
  Form,
} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Play () {
  const params = useParams();
  const token = localStorage.getItem('token');
  const sessionid = params.sessionid
  const playerid = localStorage.getItem('playerid')
  var theanswers = React.useState([]);
  var answer = React.useState([]);
  const getsession = async (id) => {
    const response = await fetch('http://localhost:5005/admin/session/' + id + '/status', {
      method: 'GET',
      headers: {
        'Content-type': 'application/json',
        Authorization: 'Bearer ' + token,
      }
    })
    const data = await response.json();
    localStorage.setItem('session', JSON.stringify(data))
  }
  const [rightanswer, setR] = React.useState([])
  const getanswer = async () => {
    const response = await fetch('http://localhost:5005/play/' + playerid + '/answer', {
      method: 'GET',
      headers: {
        'Content-type': 'application/json',
      },
    })
    const data = await response.json()
    console.log(data)
    localStorage.setItem('rightanswer', JSON.stringify(data))
  }
  const uploadanswer = async () => {
    console.log(answer)
    console.log(question.answer)
    var answerIds = []
    for (var i = 0; i < question.answer.length; i++) {
      for (var j = 0; j < answer.length; j++) {
        if (question.answer[i] === answer[j]) {
          answerIds.push(i)
        }
      }
    }
    console.log(answerIds)
    const response = await fetch('http://localhost:5005/play/' + playerid + '/answer', {
      method: 'PUT',
      headers: {
        'Content-type': 'application/json',
      },
      body: JSON.stringify({
        answerIds,
      }),
    })
    const data = await response.json()
    console.log(data)
  }
  getsession(sessionid)
  const session = JSON.parse(localStorage.getItem('session'))
  const position = session.results.position
  const question = session.results.questions[position]
  var show = React.useState('none');
  var show1 = React.useState('none');
  if (question.type === 'single choice') {
    show = 'block'
    show1 = 'none'
  } else {
    show1 = 'block'
    show = 'none'
  }
  const ifshow = {
    display: show
  }
  const ifshow1 = {
    display: show1
  }
  theanswers = question.answer
  const time = question.time
  const [timerID, setTimerID] = React.useState(null);
  const [counter, setCounter] = React.useState(time);
  React.useEffect(() => {
    if (counter > 0) {
      const timer = setTimeout(() => {
        setCounter(counter - 1)
      }, 1000);
      setTimerID(timer)
    } else {
      getanswer()
      const rightanswer1 = JSON.parse(localStorage.getItem('rightanswer'))
      setR([...answer, rightanswer1.answerIds])
      console.log(rightanswer)
    }
    return () => {
      setTimerID(null)
    }
  }, [counter]);
  if (timerID === time) {
    console.log('right')
  }
  return <>
    <div className='Title'><h1>Playing</h1></div>
    <p>
      your time:{counter}
    </p>
    <div className='Container'>
      <Form.Group className="mb-3">
        <Form.Label>Question: </Form.Label>
        <Form.Text>{question.text}</Form.Text>
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Image: </Form.Label>
        <img src={question.image} />
      </Form.Group>
      <Form.Group style={ifshow}>
        <Form.Label>Choose your answer</Form.Label>
        <Form.Text>{question.type}</Form.Text>
        <Form.Select onChange={e => { answer = [e.target.value]; uploadanswer() }}>
          {theanswers.map((item, index) => (
            <option key={index}>{item}</option>
          ))}
        </Form.Select>
      </Form.Group>
      <Form.Group style={ifshow1}>
        <Form.Label>Choose your answer</Form.Label>
        <Form.Text>{question.type}</Form.Text>
        {theanswers.map((item, index) => (
          <Form.Check
          label={item}
          type='checkbox'
          key={index + 10}
        />
        ))}
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Answer: </Form.Label>
        <Form.Text>{question.answer[rightanswer[2]]}</Form.Text>
      </Form.Group>
    </div>
  </>
}
export default Play;
