import React from 'react';
import { useParams } from 'react-router-dom';
import {
  Table,
} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function Getresult () {
  const params = useParams();
  const id = params.quizid;
  const token = localStorage.getItem('token');
  var session = React.useState();
  const getquiz = async (id) => {
    const response = await fetch('http://localhost:5005/admin/quiz/' + id, {
      method: 'GET',
      headers: {
        'Content-type': 'application/json',
        Authorization: 'Bearer ' + token,
      }
    })
    const data = await response.json();
    const session = data.active
    localStorage.setItem('session', JSON.stringify(session))
  }
  getquiz(id)
  session = JSON.parse(localStorage.getItem('session'))
  const getsession = async (id) => {
    const response = await fetch('http://localhost:5005/admin/session/' + id + '/results', {
      method: 'GET',
      headers: {
        'Content-type': 'application/json',
        Authorization: 'Bearer ' + token,
      }
    })
    const data = await response.json();
    localStorage.setItem('session', JSON.stringify(data.results))
  }
  getsession(session)
  const mysession = JSON.parse(localStorage.getItem('session'))
  console.log(mysession)
  function sum (arr) {
    var s = 0
    for (var i = 0; i < arr.length; i++) {
      if (arr[i].correct) {
        s += arr[i].correct
      }
    }
    return s
  }
  return <>
    <div className='Title'><h1>Result</h1></div>
    <Table striped bordered hover>
      <thead>
        <tr>
          <th>#</th>
          <th>First Name</th>
          <th>Score</th>
        </tr>
      </thead>
      <tbody>
        {mysession.map((item, index) => (
          <tr key={index}>
            <td>{index}</td>
            <td>{item.name}</td>
            <td>{sum(item.answers)}</td>
          </tr>
        ))}
      </tbody>
    </Table>
  </>
}
export default Getresult;
